import React from "react";
import { useState, useEffect } from "react";

import { backend_url } from "../../Components/Configurations";

const EmpHome = () => {
    const [ user,setUser] = useState();
    const [ taskList, setTasksList ] = useState();

    const userDetails = JSON.parse(sessionStorage.getItem('user'))
    const employee_id = userDetails['user']['username']
    const first_name = userDetails['user']['first_name']
    const last_name = userDetails['user']['last_name']
    const full_name = first_name.concat(" ",last_name);
    const token = userDetails['user']['token']

        useEffect(() => {
            const fetchData = async () => {
