import React from "react";
import { useState, useEffect } from "react";

import './signin.css';
import logo from "../../Assets/Icons/rabbit.png"

const SignIn = () => {
    const [ user, setUser ] = useState({
        "username": '',
        "password": ''
    }) 

    function handleChange(e){
        const { name, value } = e.target;

        setUser((prevUser) => ({
            ...prevUser,
            [name]: value
        }))
    }

    function handleSubmit(){
        console.log(user);
    }

    return(
        <div className="container d-flex justify-content-center">
            <div className="signin-body">
                <div className="mb-5 mt-5">
                    <img src={logo} alt="..loading" className="mb-4" height="75px"/>
                    <p className="fw-bold">Sign In to TaskRabbit</p>
                </div>
                <hr />
                <div className="input-body">
                    <label className="input-label">Employee Id</label>
                    <input type="text" name="username" value={user['username']} onChange={(e) => handleChange(e)} className="input-field"/>
                </div>
                <div className="input-body">
                    <label className="input-label">Password</label>
                    <input className="input-field" type="password" name="password" value={user['password']} onChange={(e) => handleChange(e)} />
                </div>
                <div>
                    <button className="button-body" onClick={handleSubmit}>Submit</button>
                </div>
            </div>
        </div>
    )
}

export default SignIn;
