import React from "react";
import { useState, useEffect } from "react";

import './signin.css';
import logo from "../../Assets/Icons/rabbit.png"

const SignIn = () => {
    const [ user, setUser ] = useState({
        "username": '',
        "password": ''
    })

    const [ userInput, setUserInput ] = useState('input-field');
    const [ passwordInput, setPasswordInput ] = useState('input-field');

    function handleChange(e){
        const { name, value } = e.target;

        setUser((prevUser) => ({
            ...prevUser,
            [name]: value
        }))
    }

    function handleSubmit(){
        console.log(user);
    }

    function handleUserInputClick(){
        setUserInput('user-field-click')
    }

    function handlePasswordInputClick(){
        setPasswordInput('password-field-click')
    }

    return(
        <div className="container signin-page">
            <div className="signin-body">
                <div className="mt-5">
                    <img src={logo} alt="..loading" className="mb-4" height="75px"/>
                    <p className="signin-header">Sign In to TaskRabbit</p>
                </div>

                <div className="input-body">
                    <label className="input-label">Employee Id</label>
                    <input type="text" name="username" value={user['username']} onChange={(e) => handleChange(e)} onClick={handleUserInputClick} className={userInput}/>
                </div>
                <div className="input-body">
                    <label className="input-label">Password</label>
                    <input className={passwordInput} type="password" name="password" value={user['password']} onChange={(e) => handleChange(e)} onClick={handlePasswordInputClick} />
                </div>
                <div>
                    <button className="button-body" onClick={handleSubmit}>Submit</button>
                </div>
            </div>
        </div>
    )
}

export default SignIn;
