import React,{ useState, useEffect } from "react";

import './taskList.css';

const TaskList = (props) => {
    const [ tasks, setTasks ] = useState([]);

    useEffect(() => {
        const taskList = Array.isArray(props.taskList) && props.taskList.length > 0 ? props.taskList[0].all_tasks : [];
        console.log('Updating allTasks:', taskList); // Verify the task list
        setTasks(taskList);
    }, [props.taskList]);


    function DateExtractor(dateString){
        const date = new Date(dateString)
        const formattedDate = date.toLocaleDateString();

        return formattedDate;
    }

    function TruncatedText(text,maxLength){
        if (text.length > maxLength){
            return text.substring(0, maxLength) + '...'
        }

        return text
    }


    return(
        <div>
            <p className="tasklist-header">Tasks List</p>
            <div className="table-design">
                <table className="table table-borderless">
                    <thead>
                        <tr>
                            <th scope="col">Raised on</th>
                            <th scope="col">ID</th>
                            <th scope="col">Ticket Title</th>
                            <th scope="col">Priority</th>
                            <th scope="col">Status</th>
                        </tr>
                    </thead>
                    <tbody>
                        { tasks && tasks.map(task => (
                            <tr key={task.task_id}>
                                <td>{DateExtractor(task.created_at)}</td>
                                <td>12345</td>
                                <td>{TruncatedText(task.summary,50)}</td>
                                <td>{task.priority}</td>
                                <td>{task.status}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    )
}

export default TaskList;
