import React from "react";
import { useState, useEffect } from "react";

import './tasks.css';
import bell from "../../Assets/Icons/notification-bell.png";
import user_logo from "../../Assets/Icons/user.png";

const Tasks = ( props ) => {
    const [allTasks, setAllTasks] = useState();
    const [currentDateTime, setCurrentDateTime] = useState(new Date());
    const [statusCounts, setStatusCounts] = useState();

    const userDetails = JSON.parse(sessionStorage.getItem('user'))
    const employee_id = userDetails['user']['username']
    const first_name = userDetails['user']['first_name']
    const last_name = userDetails['user']['last_name']
    const full_name = first_name.concat(" ",last_name);

    const countStatuses = (tasks) => {
        const counts = tasks.reduce((acc, task) => {
          acc[task.status] = (acc[task.status] || 0) + 1;
          return acc;
        }, {});
        return counts;
    };

    // Effect to set status counts on component mount
    useEffect(() => {
    const counts = countStatuses(allTasks);
    setStatusCounts(counts);
    }, []);

    useEffect(() => {
        // Function to update the current date and time
        const updateDateTime = () => {
          setCurrentDateTime(new Date());
        };

        // Set up an interval to update the time every second
        const intervalId = setInterval(updateDateTime, 1000);

        // Clean up the interval on component unmount
        return () => clearInterval(intervalId);
      }, []);


    // Function to format the date
    const formatDate = (date) => {
        const day = date.getDate();
        const month = date.toLocaleString('default', { month: 'short' });
        const weekday = date.toLocaleString('default', { weekday: 'long' });

        return `${day} ${month}, ${weekday}`;
    };

    return(
        <div className="tasks-body">
            <div className="task-header">
                <div className="left-section">
                    <div className="task-greeting">
                        Hello {first_name}
                    </div>
                    <div className="task-welcome">
                        Welcome to your task management system
                    </div>
                </div>
                <div className="right-section">
                    <p className="date">{formatDate(currentDateTime)}</p>
                    <img src={bell} alt="notification..." height="25px" className="notifications"/>
                    <img src={user_logo} alt="User profile..." height="25px" className="user-profile"/>
                </div>
            </div>
            <div className="task-unit">
                <div className="analytics-header">
                    Analytics Overview
                </div>
                <div className="analytics-overview">
                    <div className="analytics-box green-box">
                        <p className="metric-title">Todo</p>
                        <p className="metric-value">112</p>
                    </div>
                    <div className="analytics-box orange-box">
                        <p className="metric-title">In Dev</p>
                        <p className="metric-value">797</p>
                    </div>
                    <div className="analytics-box purple-box">
                        <p className="metric-title">In QA</p>
                        <p className="metric-value">596</p>
                    </div>
                    <div className="analytics-box red-box">
                        <p className="metric-title">Dev QA</p>
                        <p className="metric-value">003</p>
                    </div>
                    <div className="analytics-box red-box">
                        <p className="metric-title">QA Approved</p>
                        <p className="metric-value">003</p>
                    </div>
                    <div className="analytics-box red-box">
                        <p className="metric-title">Dev QA Approved</p>
                        <p className="metric-value">003</p>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Tasks;
