import React from "react";
import { useState, useEffect } from "react";

import './signin.css';

const SignIn = () => {
    const [ user, setUser ] = useState({
        "username": '',
        "password": ''
    }) 

    function handleChange(e){
        const { name, value } = e.target;

        setUser((prevUser) => ({
            ...prevUser,
            [name]: value
        }))
    }

    function handleSubmit(){
        console.log(user);
    }

    return(
        <div className="container">
            <div className="signin-body">
                <div className="input-body">
                    <label className="input-label">Employee Id</label>
                    <input placeholder="Username" type="text" name="username" value={user['username']} onChange={(e) => handleChange(e)} className="input-field"/>
                </div>
                <div className="input-body">
                    <label className="input-label">Password</label>
                    <input className="input-field" placeholder="Password" type="password" name="password" value={user['password']} onChange={(e) => handleChange(e)} />
                </div>
                <div>
                    <button onClick={handleSubmit}>Submit</button>
                </div>
            </div>
        </div>
    )
}

export default SignIn;
